import styled from 'styled-components'

export const segA = styled(Segment)`
  width: 1.174rem;
  height: 4.697rem;
  background: var(--dark);
  border-radius: 0.4rem;

  position: absolute;
  top: -45px;
  left: 90px;
  transform: rotate(90deg);
`
export const segB = styled(Segment)`
  width: 1.174rem;
  height: 4.697rem;
  background: var(--dark);
  border-radius: 0.4rem;

  position: absolute;
  top: -45px;
  left: 90px;
  transform: rotate(90deg);
`

export const Display = styled.div`
  position: relative;
`
