import { Content, Message, ButtonNewGame } from './style'
import { Context } from '../../context/Context'
import { useContext } from 'react'
import { ArrowClockwise } from 'phosphor-react'

function Display() {
  const context = useContext(Context)
  return (
    <Content>
      <Message>{context.message}</Message>
      <h1>{context.display}</h1>
      <h6>{context.apiNumber}</h6>
      {context.endGame && (
        <ButtonNewGame onClick={() => context.handleSetNewGame()}>
          <ArrowClockwise weight="fill" size={24} />
          NOVA PARTIDA
        </ButtonNewGame>
      )}
    </Content>
  )
}

export default Display
