import styled from 'styled-components'

export const Segment = styled.div`
  width: 11.74px;
  height: 46.97px;
  background-color: var(--dark);
  border-radius: 9999px;
  box-shadow: 0px 0px 25px #990000;
`

export const Display = styled.div`
  position: relative;
`
