import { useEffect } from 'react'
import { createContext, useState } from 'react'
import axios from 'axios'

export const Context = createContext()

export const ContextProvider = ({ children }) => {
  const [playerNumber, setPlayerNumber] = useState(0)
  const [apiNumber, setApiNumber] = useState(0)
  const [display, setDisplay] = useState('000')
  const [message, setMessage] = useState('')
  const [newGame, setNewGame] = useState(false)

  useEffect(() => {
    axios
      .get(
        'https://us-central1-ss-devops.cloudfunctions.net/rand?min=1&max=300'
      )
      .then((response) => setApiNumber(response.data.value))
    setNewGame(false)
  }, [newGame])

  function handlePlayerNumber() {
    if (typeof playerNumber !== 'number') {
      setMessage('Digite apenas números.')
      setDisplay('err')
    } else {
      switch (playerNumber) {
        case playerNumber > apiNumber:
          setMessage('É menor')
          setDisplay(playerNumber)
          break

        case playerNumber < apiNumber:
          setMessage('É maior')
          setDisplay(playerNumber)
          break

        case playerNumber === apiNumber:
          setMessage('Você acertou!')
          setDisplay(playerNumber)
          setNewGame(true)
          break

        default:
          break
      }
    }
  }

  function handleNewGame() {
    setNewGame(true)
  }

  return (
    <Context.Provider
      value={{
        playerNumber,
        apiNumber,
        setPlayerNumber,
        handlePlayerNumber,
        message,
        display,
        newGame,
        handleNewGame,
      }}
    >
      {children}
    </Context.Provider>
  )
}
