import styled from 'styled-components'

export const Segment = styled.div`
  width: 1.174rem;
  height: 4.697rem;
  //background: var(--dark);
  border-radius: 0.4rem;
`

const segA = styled(Segment)`
  background: red;
`

export const Display = styled.div`
  position: relative;
`
