import { Content, Message, ButtonNewGame, DisplayBox } from './style'
import { Context } from '../../context/Context'
import { useContext } from 'react'
import { ArrowClockwise } from 'phosphor-react'
import SegmentsDisplay from '../SegmentsDisplay'

function Display() {
  const context = useContext(Context)
  const { apiNumber, display, playerNumber, handleSetNewGame, numbersMap } =
    context
  const arrayDisplay = Array.from(display)

  return (
    <Content>
      <Message>{context.message}</Message>

      <DisplayBox>
        {numbersMap.map((nMap, idx) =>
          arrayDisplay.map((nDisp) => {
            if (nDisp) {
              return <SegmentsDisplay />
            }
          })
        )}
      </DisplayBox>

      {context.endGame && (
        <ButtonNewGame onClick={() => handleSetNewGame()}>
          <ArrowClockwise
            weight="bold"
            size={20}
            style={{ marginRight: '4px' }}
          />
          NOVA PARTIDA
        </ButtonNewGame>
      )}
    </Content>
  )
}

export default Display
