import { useEffect } from 'react'
import { createContext, useState } from 'react'
import axios from 'axios'

export const Context = createContext()

export const ContextProvider = ({ children }) => {
  const [playerNumber, setPlayerNumber] = useState(0)
  const [apiNumber, setApiNumber] = useState(0)
  const [display, setDisplay] = useState('000')
  const [message, setMessage] = useState('')
  const [newGame, setNewGame] = useState(false)

  useEffect(() => {
    handleNewGame()
  }, [newGame])

  function handleNewGame() {
    axios
      .get(
        'https://us-central1-ss-devops.cloudfunctions.net/rand?min=1&max=300'
      )
      .then((response) => setApiNumber(response.data.value))
    setNewGame(false)
  }

  function handleSetNewGame() {
    setNewGame(true)
  }

  function handlePlayerNumber() {
    if (isNaN(playerNumber)) {
      setMessage('Digite apenas números.')
      setDisplay('err')
    } else {
      if (parseInt(playerNumber) > apiNumber) {
        setMessage('É menor')
        setDisplay(playerNumber)
        return
      } else if (parseInt(playerNumber) < apiNumber) {
        setMessage('É maior')
        setDisplay(playerNumber)
        return
      } else if (parseInt(playerNumber) === apiNumber) {
        setMessage('Você acertou!')
        setDisplay(playerNumber)
        return
      } else {
        setMessage('Ooops, isto não deveria ter acontecido!')
        setDisplay('err')
      }
    }
  }

  return (
    <Context.Provider
      value={{
        playerNumber,
        apiNumber,
        setPlayerNumber,
        handlePlayerNumber,
        message,
        display,
        newGame,
        handleNewGame,
        handleSetNewGame,
      }}
    >
      {children}
    </Context.Provider>
  )
}
