import { Content, Message, ButtonNewGame, DisplayBox } from './style'
import { Context } from '../../context/Context'
import { useContext } from 'react'
import { ArrowClockwise } from 'phosphor-react'
import SegmentsDisplay from '../SegmentsDisplay'

function Display() {
  const context = useContext(Context)
  const { apiNumber, display, playerNumber, handleSetNewGame, numbersMap } =
    context
  const arrayPlayerNumber = Array.from(playerNumber)

  console.log('api', apiNumber)
  console.log('arrayPlayerNumber', arrayPlayerNumber)

  return (
    <Content>
      <Message>{context.message}</Message>

      <DisplayBox>
        {numbersMap.forEach((number) => {
          arrayPlayerNumber.forEach((playerNumber) => {
            if (Object.keys(number) * 1 === playerNumber * 1) {
              ;<SegmentsDisplay props={Object.values(number)} />
            }
          })
        })}
      </DisplayBox>

      {context.endGame && (
        <ButtonNewGame onClick={() => handleSetNewGame()}>
          <ArrowClockwise
            weight="bold"
            size={20}
            style={{ marginRight: '4px' }}
          />
          NOVA PARTIDA
        </ButtonNewGame>
      )}
    </Content>
  )
}

export default Display
