import styled from 'styled-components'

export const Segment = styled.div`
  width: 1.174rem;
  height: 4.697rem;
  background: var(--dark);
  border-radius: 0.4rem;
`

export const segA = styled(Segment)`
  width: 1.174rem;
  height: 4.697rem;
  background: var(--dark);
  border-radius: 0.4rem;
`

export const Display = styled.div`
  position: relative;
`
