import styled from 'styled-components'

export const Content = styled.section`
  flex: 1;
  width: 100%;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
`
export const Message = styled.p`
  font-weight: 700;
  font-size: 16px;
  line-height: 20px;
  text-align: center;
  letter-spacing: -1px;
  margin-bottom: 60px;
`
export const ButtonNewGame = styled.button`
  width: 130px;
  height: 42px;
  display: flex;
  flex-direction: row;
  align-items: center;
  justify-content: center;
  flex-wrap: nowrap;

  background: linear-gradient(180deg, #434854 0%, #9e9e9e 100%);
  border: none;
  border-radius: 4px;
  color: var(--white);
  font-weight: 700;
  font-size: 12px;
  line-height: 14px;

  margin-top: 60px;
`

export const DisplayBox = styled.div`
  height: 100%;
`
