import { Content, Message, ButtonNewGame, DisplayBox } from './style'
import { Context } from '../../context/Context'
import { useContext } from 'react'
import { ArrowClockwise } from 'phosphor-react'
import SegmentsDisplay from '../SegmentsDisplay'
import numbers from '../../assets/numbers'

function Display() {
  const context = useContext(Context)
  console.log('api', context.apiNumber)
  console.log('display', context.display)

  return (
    <Content>
      <Message>{context.message}</Message>

      <DisplayBox>
        {' '}
        {numbers}
        <SegmentsDisplay />
      </DisplayBox>

      {context.endGame && (
        <ButtonNewGame onClick={() => context.handleSetNewGame()}>
          <ArrowClockwise
            weight="bold"
            size={20}
            style={{ marginRight: '4px' }}
          />
          NOVA PARTIDA
        </ButtonNewGame>
      )}
    </Content>
  )
}

export default Display
