import styled from 'styled-components'

export const Container = styled.main`
  margin: auto;
  margin-top: 2rem;
  display: flex;
  flex-direction: column;
  align-items: center;
  width: 365px;
  height: 588px;
  padding: 30px;

  background: linear-gradient(180deg, #eeeeee 0%, #ffffff 100%);
`
